// MÃ‰TODOS: Listado no requiere de mÃ©todos.
// PROPS: Listado recibe el mÃ©todo heredado de App y se lo pasa a cada Item.

export default function Tarjeta(props) {
  return (
    <ul>
      {listadoPlataformas.map((plataforma, index) => (
        <Item
